/**
 * 项目页面
 */
import '@/style/ProjectView.scss'
import React, { useEffect, useState } from 'react'
import { Card, Image, List, message, Tooltip } from 'antd'
import { LoadingOutlined } from '@ant-design/icons'
import Api from '@/api'
import AppConfig from '@/utils/config'

const ProjectView: React.FC = props => {
    /** 项目列表 */
    const [projects, setProjects] = useState<ApiResp.ArticleModel[]>([])
    const [pageIndex, setPageIndex] = useState(1)
    const [total, setTotal] = useState(0)
    const [isLoading, setLoading] = useState(false)

    useEffect(() => {
        !isLoading && requestProjects(0, pageIndex)
    }, [])

    /** 请求项目列表数据 */
    const requestProjects = (cid: number, pageIndex: number): void => {
        setLoading(true)
        Api.projects(cid, pageIndex, AppConfig.pageSize)
            .then(res => {
                if (res.data.curPage !== pageIndex) return
                const array = res.data.datas || []
                setProjects(array)
                setTotal(res.data.total)
            })
            .catch((e: Error) => message.error(e.message))
            .finally(() => setLoading(false))
    }

    /** 页码切换的回调 */
    const onHandlePageChange = (pageIndex: number, pageSize: number): void => {
        setPageIndex(pageIndex)
        requestProjects(0, pageIndex)
    }

    return (
        <div className="project-view">
            <List
                grid={{
                    gutter: 16,
                    xs: 1,
                    sm: 2,
                    md: 3,
                    lg: 4,
                    xl: 5,
                    xxl: 6
                }}
                dataSource={projects}
                pagination={{
                    onChange: onHandlePageChange,
                    pageSize: AppConfig.pageSize,
                    total: total,
                    showQuickJumper: true,
                    showSizeChanger: false
                }}
                renderItem={item => (
                    <List.Item key={item.id}>
                        <Card
                            hoverable
                            // style={{ width: 220 }}
                            cover={
                                <Image
                                    className="thumb-image"
                                    // width={220}
                                    height={260}
                                    src={item.envelopePic}
                                    placeholder={<LoadingOutlined />}
                                    fallback="data:image/png;base64,"
                                />
                            }
                        >
                            <a href={item.link} target="_blank">
                                <Tooltip title={item.title}>
                                    <Card.Meta title={item.title} description={item.desc} />
                                </Tooltip>
                            </a>
                        </Card>
                    </List.Item>
                )}
            ></List>
        </div>
    )
}

export default ProjectView
